using MangaForum.Areas;
using MangaForum.Data;
using MangaForum.Models;
using MangaForum.Models.Utilities.Requests;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Identity;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Mvc.RazorPages;
using Microsoft.Extensions.Logging;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace MangaForum.Pages
{
    [Authorize]
    public class CreateTopicModel : PageModel
    {
        private readonly MangaIdentityDbContext _context;
        private readonly UserManager<ForumUser> _manager;
        public CreateTopicModel(MangaIdentityDbContext context, UserManager<ForumUser> manager)
        {
            this._context = context;
            this._manager = manager;
            MangaTitleTrovato = "";
        }

        [BindProperty]
        public ForumTopic Topic { get; set; }
        [BindProperty]
        public Manga Manga { get; set; }
        [BindProperty]
        
        public string FirstPostMessage { get; set; }
        public string MangaTitleTrovato { get; set; }

        private readonly string _ImgOnError = "";

        public async Task<IActionResult> OnGetAsync()
        {
            if (Topic != null)
                Manga = APICaller.GetMangaByName(Topic.Manga.Title).Result.mangas.First();
            else
            {
                Manga = new Manga
                {
                    Image = _ImgOnError,
                    Title = "Not Found",
                };
            }



            return Page();
        }
        public bool check = false;
        public async Task<IActionResult> OnPostAsync(string Button)
        {
            if(((Topic.Manga.Title == null || Topic.Name == null || FirstPostMessage == null) && Button == null) || Topic.Manga.Title == null )
            {
                return Page();
            }
            
            if (APICaller.GetMangaByName(Topic.Manga.Title).Result.state)
                Manga = APICaller.GetMangaByName(Topic.Manga.Title).Result.mangas.First();
            else
            {
                MangaTitleTrovato = "Not Found!";
                Manga.Image = _ImgOnError;
                return Page();
            }
            if (Button != null)
            {
                MangaTitleTrovato = Manga.Title;
                return Page();
            }

            var topicr = APICaller.CreateTopic(new CreateTopicRequest { id_Manga = Manga.id_Manga, Name = Topic.Name }).Result;
            int user = _manager.GetUserAsync(User).Result.id_User;
            var post = APICaller.CreatePost(new CreatePostRequest { id_Creator = user, id_Topic = topicr.topic.id_Topic, Message = FirstPostMessage }).Result;
            if (post.state && topicr.state)
                return Redirect("/Posts?id=" + post.post.id_Post);
            else
                return Redirect("/Error");






        }
    }
}
